mapper.select:
SELECT id, source, last_update_dttm, created
FROM gfi.e_event
WHERE source = #{source}

mapper.insert:
INSERT INTO gfi.e_event (source, last_update_dttm, created)
VALUES (#{source}, #{lastUpdateDttm}, #{created})
ON CONFLICT (source) DO UPDATE SET last_update_dttm = #{lastUpdateDttm}
